mapper.select:
     select * from CertificationView where goalId=#{goalId}  

     and name = #{name}  


     and date_format(regdate, '%Y-%m-%d') >=#{startDate} 


     and date_format(regdate, '%Y-%m-%d') <=#{endDate} 

	order by regdate desc;

select * from CertDetailView where goalId=#{goalId} 
and regdate < (select regdate from CertDetailView where id=#{id}) 
order by regdate desc 
limit 1;

select * from CertDetailView where goalId=#{goalId} 
and regdate > (select regdate from CertDetailView where id=#{id}) 
order by regdate asc 
limit 1;

select * from CertificationView C 

where C.goalId = #{param1}
order by C.userId;
