mapper.update:
UPDATE Goal
SET title = #{title}, explanation = #{explanation}, mainImage = #{mainImage}
WHERE id = #{id}

mapper.select:
      select * from GoalView where publicStatus=1

		and categories like concat('%',replace(#{categories}, ',','%'),'%')


		and date_format(startDate, '%Y-%m-%d') >=#{startDate} 


		and date_format(endDate, '%Y-%m-%d')<=#{endDate} 


		and days like concat('%',replace(#{days}, ',','%'),'%') 


		and totalParticipants = #{totalParticipants} 


		and title like '%${query}%';

      select GAV.*
		from GoalAllView GAV join (
				select TPS.*
				from(
					select @row_num:=@row_num+1 id, TP.*, U.profile, U.name
					from(
						select id goalId, userId from Goal
						union
						select goalId, userId from Participation
						union
						select goalId, requestReceiveUserId userId from `Group`
						where answer = 1
					) TP left join User U on TP.userId = U.id,
					(SELECT @row_num:=0) AS RN
					order by goalId, userId
				) TPS
				where TPS.userId = #{userId}
			) TPV
		on TPV.goalId = GAV.id;


    select G.*,PV.name,PV.profile from (SELECT P.id , P.userId , P.goalId , U.name ,U.profile
			FROM Participation P
            LEFT JOIN User U ON P.userId = U.id) PV left join Goal G on PV.goalId = G.Id where G.id = ${param1};

select * from Goal
where userId = #{userId}
order by regdate desc
limit 1;
